function [ ] = SetLegendOrder( newOrder )    legend = findall(gcf,'type','axes','Tag','legend');    string = get(legend,'String');    newString = string;    for i=1:length(newString),        newString(i) = string(newOrder(i));    end    set(legend,'String',newString);    leg_line=findobj(legend,'type','Line');    ydata = zeros(length(newOrder),1);    lind=1;    for l=1:length(leg_line),        line = leg_line(l);        if (strcmp(get(line,'LineStyle'),'none')==1)            ydata(lind,:) = get(line,'YData');            set(line,'Color',[0,0,1]);            lind = lind+1;        end    end    sortedLine = sortrows(ydata,[1]);    for l=1:length(leg_line),        line = leg_line(l);        y = get(line,'YData');        currentPosition = sortedLine==y(1);        nextPosition = newOrder(currentPosition);        set(line,'YData',repmat(sortedLine(nextPosition),1,length(y)));    end    end